var UINestable = function () {
    var t = function (t) {
        t.stopPropagation();
        var e = t.length ? t : $(t.target), a = e.data("output");
        var data = $(t.target).nestable("serialize");

        $.ajax({
            url: '/admin/menu/update-state',
            data: {
                list: e.nestable('serialize')
            },
            type: 'post',
            success: function()
            {
                $('#nestable_list_1').nestable('destroy');
            }
        });
    };
    return {
        init: function () {
            $("#nestable_list_1").nestable({group: 1}).on("change", t);
        }
    }
}();
jQuery(document).ready(function () {
    UINestable.init()
});